<button id="btnNew" type="button" class="btn btn-outline-primary btn-xs daBtnTool" data-toggle="tooltip"
    data-placement="bottom" title="New" value="New"><i class="far fa-star"></i></button>
<button id="btnSave" type="button" class="btn btn-outline-primary btn-xs daBtnTool" data-toggle="tooltip"
    data-placement="bottom" title="Save" value="Save" disabled><i class="far fa-save"></i></button>
<button id="btnRefresh" type="button" class="btn btn-outline-secondary btn-xs daBtnTool" data-toggle="tooltip"
